import { useState } from "react";
import { useNavigate, NavLink } from "react-router-dom";
import axios from "axios";
import styles from "./Login.module.css";

const Login = () => {
  const navigate = useNavigate();
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [otp, setOtp] = useState("");
  const [otpSent, setOtpSent] = useState(false);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");

  // Send OTP to phone number
  const sendOtp = async () => {
    try {
      await axios.post("http://localhost:5000/api/otp/send-otp", { phone });
      setOtpSent(true);
      setMessage("OTP sent successfully!");
      setError("");
    } catch (error) {
      setError("Failed to send OTP. Please check the phone number.");
    }
  };

  // Verify OTP and login
  const verifyOtp = async () => {
    try {
      const { data } = await axios.post("http://localhost:5000/api/otp/verify-otp", { phone, otp });

      // Store user details in localStorage
      localStorage.setItem("token", data.token);
      localStorage.setItem("role", data.role);
      localStorage.setItem("userName", data.name);
      localStorage.setItem("profilePic", data.profilePic);

      setMessage("Login successful!");
      navigate(data.role === "admin" ? "/admin-dashboard" : "/user-dashboard");
    } catch (error) {
      setError("Invalid OTP. Please try again.");
    }
  };

  // Email & Password Login
  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const { data } = await axios.post("http://localhost:5000/api/auth/login", { email, password });

      // Store user details in localStorage
      localStorage.setItem("token", data.token);
      localStorage.setItem("role", data.role);
      localStorage.setItem("userName", data.name);
      localStorage.setItem("profilePic", data.profilePic);

      setMessage("Login successful!");
      navigate(data.role === "admin" ? "/admin-dashboard" : "/user-dashboard");
    } catch (error) {
      setError("Invalid email or password. Please try again.");
    }
  };

  return (
    <div className={styles.loginContainer}>
       
 

      <hr />

      {/* Email & Password Login Section */}
      <form onSubmit={handleLogin}>
        <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
        <button type="submit">Login</button>
      </form>

      {/* Display Messages */}
      {message && <p className={styles.message}>{message}</p>}
      {error && <p className={styles.error}>{error}</p>}

      <p>
        Donâ€™t have an account? <NavLink to={"/register"}>Sign Up</NavLink>
      </p>
      </div>
   
  );
};

export default Login;
