import React, { useState } from "react";
import axios from "axios";
import "./loanApplication.css"

const LoanApplication = () => {
  const [amount, setAmount] = useState("");
  const [repaymentDate, setRepaymentDate] = useState("");
  // const [num, setNum] = useState("");
  // const [loantype, setLoantype] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      // await axios.post("http://localhost:5000/api/loans", { amount, repaymentDate }, {
      //   headers: { Authorization: token },
      // });
      alert("Loan Application Submitted");
    } catch (err) {
      alert("Application Failed");
    }
  };

  return (
    <div>
      
      <form onSubmit={handleSubmit} className="loanapp-container">
      <h2>Apply for Loan</h2>
        <label>Loan-Type:</label>
        {/* <select value={loantype} onChange={(e) => setLoantype(e.target.value)} required>
          <option>personal loan</option>
          <option>Student loan</option>
          <option>Car loan</option>
          <option>Home loan</option>
          <option>Gold loan</option>
        </select> */}
        <input type="number" placeholder="Loan Amount" value={amount} onChange={(e) => setAmount(e.target.value)} required />


        {/* <input type="number" placeholder="Enter Your Phone No." value={num} onChange={(e) => setNum(e.target.value)} required/>
        <label>Enter a document proof </label>
        <input type="file"/> */}
        <input type="date" placeholder="Repayment Date" value={repaymentDate} onChange={(e) => setRepaymentDate(e.target.value)} required />
        <button type="submit">Apply</button>
      </form>
    </div>
  );
};

export default LoanApplication;
