const AdminDashboard = () => {
  const [loans, setLoans] = useState([]);

  // Fetch all loan requests
  useEffect(() => {
      axios.get("http://localhost:5000/api/loans") // Fetch loans
          .then(response => setLoans(response.data))
          .catch(error => console.error("Error fetching loans:", error));
  }, []);

  // Approve Loan Function
  const approveLoan = async (loanId) => {
      try {
          await axios.put(`http://localhost:5000/api/loans/approve/${loanId}`);
          alert("Loan Approved Successfully!");
          setLoans(loans.map(loan => 
              loan._id === loanId ? { ...loan, status: "approved" } : loan
          ));
      } catch (error) {
          console.error("Error approving loan:", error);
      }
  };

  return (
      <div>
          <h2>Admin Dashboard - Loan Approvals</h2>
          <table border="1">
              <thead>
                  <tr>
                      <th>Applicant</th>
                      <th>Amount</th>
                      <th>Status</th>
                      <th>Action</th>
                  </tr>
              </thead>
              <tbody>
                  {loans.map((loan) => (
                      <tr key={loan._id}>
                          <td>{loan.user.name}</td>
                          <td>${loan.amount}</td>
                          <td>{loan.status}</td>
                          <td>
                              {loan.status === "pending" ? (
                                  <button onClick={() => approveLoan(loan._id)}>Approve</button>
                              ) : (
                                  <span>Approved</span>
                              )}
                          </td>
                      </tr>
                  ))}
              </tbody>
          </table>
      </div>
  );
};

export default AdminDashboard;
