import { useState, useEffect } from "react";
import axios from "axios";
import ReactCrop from "react-image-crop";
import "react-image-crop/dist/ReactCrop.css";
import styles from "./UserProfile.module.css";

const DEFAULT_AVATAR = "https://via.placeholder.com/100";

const UserProfile = () => {
  const [profilePic, setProfilePic] = useState(localStorage.getItem("profilePic") || DEFAULT_AVATAR);
  const [, setSelectedFile] = useState(null);
  const [image, setImage] = useState(null);
  const [crop, setCrop] = useState({ aspect: 1, width: 50 });
  const [croppedImage, setCroppedImage] = useState(null);
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [message, setMessage] = useState("");

  useEffect(() => {
    const storedProfilePic = localStorage.getItem("profilePic");
    if (storedProfilePic) setProfilePic(storedProfilePic);
  }, []);

  useEffect(() => {
  async function fetchUserData() {
    try {
      const { data } = await axios.get("http://localhost:5000/api/users/profile");
      setName(data.name);
      setProfilePic(data.profilePic || DEFAULT_AVATAR);
    } catch (error) {
      console.error("Error fetching user data", error);
    }
  }
  fetchUserData();
}, []);


const handleUpdateProfile = async () => {
    try {
      await axios.put("http://localhost:5000/api/users/update-profile", { name });
      setMessage("Profile updated successfully!");
    } catch (error) {
      setMessage("Failed to update profile.");
    }
  };

  const handleChangePassword = async () => {
    if (password !== confirmPassword) {
      setMessage("Passwords do not match.");
      return;
    }

    try {
      await axios.put("http://localhost:5000/api/users/change-password", { password });
      setMessage("Password changed successfully!");
      setPassword("");
      setConfirmPassword("");
    } catch (error) {
      setMessage("Failed to change password.");
    }
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => setImage(reader.result);
    }
  };

  const handleCropComplete = (crop) => {
    if (image && crop.width && crop.height) {
      const canvas = document.createElement("canvas");
      const imageElement = new Image();
      imageElement.src = image;

      imageElement.onload = () => {
        const scaleX = imageElement.naturalWidth / imageElement.width;
        const scaleY = imageElement.naturalHeight / imageElement.height;
        canvas.width = crop.width;
        canvas.height = crop.height;
        const ctx = canvas.getContext("2d");

        ctx.drawImage(
          imageElement,
          crop.x * scaleX,
          crop.y * scaleY,
          crop.width * scaleX,
          crop.height * scaleY,
          0,
          0,
          crop.width,
          crop.height
        );

        canvas.toBlob((blob) => {
          setCroppedImage(URL.createObjectURL(blob));
        });
      };
    }
  };

  const handleUpload = async () => {
    if (!croppedImage) return;

    const blob = await fetch(croppedImage).then((res) => res.blob());
    const formData = new FormData();
    formData.append("profilePic", blob, "profile.jpg");

    try {
      const { data } = await axios.post("http://localhost:5000/api/users/upload-profile", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      localStorage.setItem("profilePic", data.profilePic);
      setProfilePic(data.profilePic);
      setImage(null);
      setCroppedImage(null);
    } catch (error) {
      console.error("Profile picture upload failed", error);
    }
  };

  const handleRemove = async () => {
    try {
      await axios.post("http://localhost:5000/api/users/remove-profile");

      localStorage.removeItem("profilePic");
      setProfilePic(DEFAULT_AVATAR);
    } catch (error) {
      console.error("Profile picture removal failed", error);
    }
  };

  return (
    <div className={styles.profileContainer}>
      <h2>User Profile Settings</h2>
      {message && <p className={styles.message}>{message}</p>}

    {/* Change Name */}
    <div className={styles.profileField}>
        <label>Name:</label>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        <button onClick={handleUpdateProfile}>Update Name</button>
      </div>

       {/* Change Password */}
       <div className={styles.profileField}>
        <label>New Password:</label>
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
      </div>
      <div className={styles.profileField}>
        <label>Confirm Password:</label>
        <input type="password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} />
      </div>
      <button onClick={handleChangePassword}>Change Password</button>

      <div className={styles.profileSection}>
        <img src={profilePic} alt="Profile" className={styles.profilePic} />
        <input type="file" accept="image/*" onChange={handleFileChange} />
        {image && (
          <div className={styles.cropContainer}>
            <ReactCrop src={image} crop={crop} onChange={(newCrop) => setCrop(newCrop)} onComplete={handleCropComplete} />
          </div>
        )}
        {croppedImage && <img src={croppedImage} alt="Cropped Preview" className={styles.croppedPreview} />}
        {croppedImage && <button onClick={handleUpload}>Upload Cropped Picture</button>}
        {profilePic !== DEFAULT_AVATAR && <button onClick={handleRemove} className={styles.removeButton}>Remove Picture</button>}
      </div>
    </div>
  );
};

export default UserProfile;
