import React, { useEffect, useState } from "react";
import axios from "axios";

const UserDashboard = () => {
  const [loans, setLoans] = useState([]);

  useEffect(() => {
    const fetchLoans = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get("http://localhost:5000/api/loans", {
          headers: { Authorization: token },
        });
        setLoans(res.data);
      } catch (err) {
        alert("Failed to fetch loan data");
      }
    };
    fetchLoans();
  }, []);

  return (
    <div>
      <h2>Your Loan Applications</h2>
      <ul>
        {loans.map((loan) => (
          <li key={loan._id}>
            User: {loan.userId} | Name:{loan.name} | Loan Type:{loan.loantype} | Amount: â‚¹{loan.amount} | Number:{loan.num} | Status: {loan.status} | File:{loan.file} | Repayment Date: {new Date(loan.repaymentDate).toDateString()}
            
            <tr>
              <th>User</th>
              <th>Name</th>
              <th>Loan Type</th>
              <th>Amount</th>
              <th>Number</th>
              <th>Status</th>
            </tr>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UserDashboard;
